tinymce.PluginManager.add('jbimages',function(editor,url){function jbBox(){editor.windowManager.open({title:'Fazer o upload de uma imagem:',file:url+'/dialog-v4.htm',width:350,height:135,buttons:[{text:'Subir imagem',classes:'widget btn primary first abs-layout-item',disabled:true,onclick:'close'},{text:'Fechar',onclick:'close'}]})}editor.addButton('jbimages',{tooltip:'Upload an image',icon:'image',text:'Upload',onclick:jbBox});editor.addMenuItem('jbimages',{text:'Subir uma imagem',icon:'image',context:'insert',onclick:jbBox})});